class Solution {
     int ans = 0;
     Stack<Integer> positiveDiagonal = new Stack<>();
     Stack<Integer> negativeDiagonal = new Stack<>();

    public  int totalNQueens(int n) {
        int[][] board = new int[n][n];
        Stack<Integer> row = new Stack<>();
        for (int i = 0; i < n; i++) {
            row.push(i);
            positiveDiagonal.push(i);
			// Inserting the value of diagonol
            negativeDiagonal.push(-i);
            solve(1, row, n-1,n);
			//Checking the board for each position in the first place then traverse the remaining  place
            positiveDiagonal.pop();
            negativeDiagonal.pop();
            row.pop();
        }
        return ans;
    }

    public  void solve(int start, Stack<Integer> row, int size, int n) {

        if (size==0) {
            ans += 1;
            return;
        }

        for (int i = 0; i < n; i++) {
		// Check each element if that postion is good or not
            if (verifyPosition(start, i, row)) {
                row.push(i);
                positiveDiagonal.push(start + i);
                negativeDiagonal.push(start - i);
                solve(start + 1, row, size-1,n);
                positiveDiagonal.pop();
                negativeDiagonal.pop();
                row.pop();
            }
        }
    }

    public  boolean verifyPosition(int start, int i, Stack<Integer> row) {

        return !(row.contains(i) || positiveDiagonal.contains(start + i) || negativeDiagonal.contains(start - i));
    }
}
