class Solution:
    def totalNQueens(self, n: int) -> int:

        def soln(row, diag1, diag2, cols):
            #if passing all the rows and columns of the grid then return 1 i.e. possible answer
            if row==n:
                return 1

            tot = 0
            for col in range(n):
                #checking for the grid of queen not in same diagonals and same columns 
                if ((col-row) in diag1) or ((col+row) in diag2) or (col in cols):
                    continue

                #if passed above condition then including this grid in diagonals and columns for next row
                tot += soln(row+1, diag1|set([col-row]), diag2|set([col+row]), cols|set([col]))

            #Finally returning total no. of soluntions
            return tot

        #taking starting from row 0 sets of diagonal1 , diagonal2, columns
        return soln(0, set(), set(), set())
