var totalNQueens = function(n) {
    let res = 0;
    let board = [];
    function backtrack(r) {
        if(r === n) {
            res++;
            return;
        }
        for(let c = 0 ; c < n ; c++) {
            if(!board.some((bc, br) => 
                          bc === c || bc - br === c - r || bc + br === c + r)) {
                board.push(c);
                backtrack(r + 1);
                board.pop();
            }
        }
    }
    backtrack(0);
    return res;
};

