        for(int i = 0; i<size; i++){
            if(checkValid(board, row, i, size)){
                board[row][i] = 'Q';
                helper(board, row+1, size);
                board[row][i] = '.';
            }
        }
    }
    int totalNQueens(int n) {
        string str;
        str.append(n, '.');
        vector<string> board(n, str);
        count = 0;
        helper(board, 0, n);
        return count;
    }
};
