class Solution {
public:


    bool check(int row, int col, vector<int> &queens, int &n){
        for(int i = 0; i < row; i++){
            int prev_row = i;
            int prev_col = queens[i];

            if(prev_col == col || abs(row-prev_row) == abs(col - prev_col)){
                return 0;
            }
        }
        return 1;
    }

    int solve(int level, int &n, vector<int> &queens){

        if(level == n){
            return 1;
        }
       int ans = 0;
        for(int col = 0; col < n; col++){
            if(check(level, col, queens, n)){
                queens[level] = col;
                ans += solve(level + 1, n, queens);
                queens[level] = -1;
            }
        }

        return ans;
    }

    int totalNQueens(int n) {
        vector<int> queens(n,-1);
        int res = solve(0, n, queens);

        return res;

    }
};
```

