        int max = Integer.MIN_VALUE, prodMax = 1, prodMin = 1;
        for(int i = 0;i < nums.length;i++) { 
            int posVal = prodMax * nums[i], negVal = prodMin * nums[i];
            prodMax = Math.max(Math.max(posVal, nums[i]), negVal);
            prodMin = Math.min(Math.min(posVal, nums[i]), negVal);
            max = Math.max(max, prodMax);
        }
        return max;
