class Solution {
public:

    int maxProduct(vector<int>& nums) {
        int n = nums.size();
        if (n == 0) return 0;
        int Min[2] = {nums[0], 0}, Max[2] = {nums[0], 0};
        int ans = Max[0], i;
        for (i = 1; i < n; i++) {
            Max[i % 2] = max(max(Max[(i - 1) % 2] * nums[i], nums[i]), Min[(i - 1) % 2] * nums[i]);
            Min[i % 2] = min(min(Max[(i - 1) % 2] * nums[i], nums[i]), Min[(i - 1) % 2] * nums[i]);
            ans = max(ans, Max[i % 2]);
        }
        return ans;
    }
};
