# C++
```
class Solution {
public:
    string categorizeBox(long long l, long long w, long long h, int mass) {
        long long volume = l*w*h, b1 = 0, b2 = 0;
        if(l>=1e4 || w>=1e4 || h>=1e4 || volume>=1e9)  b1 = 1;      // for Bulky
        if(mass>=100)                                  b2 = 1;      // for heavy
        if(b1+b2==2)                                   return "Both"; 
