# Code
```
public class Solution {
    private const int MaxMass = 100;
    private const int MaxDimension = 10_000;
    private const int MaxVolume = 1_000_000_000;

    public string CategorizeBox(int l, int w, int h, int m) 
    {
        var isBulky = (long)l * w * h >= MaxVolume || 
            new [] { l, w, h }.Any(d => d >= MaxDimension);

        return (isBulky, isHeavy : m >= MaxMass) switch 
            {
                ( true,  true) => "Both",
                ( true, false) => "Bulky",
                (false,  true) => "Heavy",
                            _ => "Neither"
            };
    }
}
```

