class Solution {
public:

    bool is_bulky(int length, int width, int height)
    {
        long long volume = ((long long) length * (long long) width * (long long) height);

        return volume >= pow(10, 9) || length >= pow(10, 4) || width >= pow(10, 4) || height >= pow(10, 4);
    }

    bool is_heavy(int mass)
    {
        return mass >= 100;
    }

