function categorizeBox(length: number, width: number, height: number, mass: number): string {
    let bulky = false
    let heavy = false
    if (length >= Math.pow(10,4) || width >= Math.pow(10,4) || height >= Math.pow(10,4) || length * width * height >= Math.pow(10,9) ){
        bulky = true
    }
    if (mass >= 100){
        heavy = true
    }
    if (bulky && heavy){
        return "Both"
    } else if (bulky == false && heavy == false){
        return "Neither"
    } else if (bulky && heavy == false){
        return "Bulky"
    } else {
        return "Heavy"
    }
};
```

