class Solution {
public:
    bool checkbulky(int length, int width, int height, int mass){
        vector<long long> v;
        int count=0;
        if(length>=pow(10,4) || width>=pow(10,4) || height>=pow(10,4) || mass>=pow(10,4)){
            count++;
        }
        v.push_back(length);
        v.push_back(width);
        v.push_back(height);
        if(v[0]*v[1]*v[2] >=pow(10,9)){
            count++;
        }
        if(count==1 ||count==2){
            return true;
        }
        else{
            return false;
        }

    }
    bool checkheavy(int mass){
        if(mass>=100){
            return true;
        }
        else{
            return false;
        }
    }
    string categorizeBox(int length, int width, int height, int mass) {
        bool ans=checkbulky(length,width,height,mass);
        bool res=checkheavy(mass);
        if(ans==true && res==true){
            return "Both";
        }
        if(ans==false && res==false){
            return "Neither";
        }
        if(ans==true && res==false){
            return "Bulky";
        }
      else if(ans==false && res==true){
            return "Heavy";
      }
        return "";
    }
};
