    public String categorizeBox(int length, int width, int height, int mass) {
        long volume = (length*1L)*(width*1L)*(height*1L);
        System.out.println(volume);

        String ans = "Bulky";

        if(length >= 10000 || width >=10000 || height >=10000 || volume >=1000000000L){   
            if(mass>=100){
                ans = "Both";
            }else{
                ans = "Bulky";
            }
        }else if(mass >= 100){
            ans= "Heavy";
        }else{
           ans =  "Neither";
        }

        return ans;
    }
}
