public class Solution {
    public string CategorizeBox(int length, int width, int height, int mass)
    {
        bool b = new[] { length, width, height, mass }.Max() >= 1e4 || 1L * length * width * height >= 1e9;
        bool h = mass >= 1e2;

        return (b, h) switch
        {
            (true, true) => "Both",
            (true, _) => "Bulky",
            (_, true) => "Heavy",
            _ => "Neither"
        };
    }
}
