public class Box {
    private final long length;
    private final long width;
    private final long height;
    private final int mass;

    public Box(int length, int width, int height, int mass) {
        this.length = length;
        this.width = width;
        this.height = height;
        this.mass = mass;
    }

    public boolean isBulky() {
        return isAnyDimensionGreaterThan1e4()
                || isVolumeGreaterThan1e9();
    }

    public boolean isHeavy() {
        return mass >= 100;
    }

    private boolean isAnyDimensionGreaterThan1e4() {
        return length >= 10000
                || width >= 10000
                || height >= 10000;
    }

    private boolean isVolumeGreaterThan1e9() {
        return length*width*height >= 1000000000;
    }
}

