import kotlin.math.pow

class Solution {
    companion object {
        private val BULKY_DIMENSION_LIMIT = 10.0.pow(4.0)
        private val BULKY_VOLUME_LIMIT = 10.0.pow(9.0)
        private const val HEAVY_MASS_LIMIT = 100
    }

    fun categorizeBox(length: Int, width: Int, height: Int, mass: Int): String {
        val categories = mutableSetOf<String>()

        if (arrayOf(length, width, height).any { it >= BULKY_DIMENSION_LIMIT }
            || length.toDouble() * width.toDouble() * height.toDouble() >= BULKY_VOLUME_LIMIT) {
            categories.add("Bulky")
        }

        if (mass >= HEAVY_MASS_LIMIT) {
            categories.add("Heavy")
        }

        categories.apply {
            if (isEmpty()) return "Neither"
            if (size == 2) return "Both"
            return first()
        }
    }
}
```

