class Solution {
public:
    int xorBeauty(vector<int>& nums) {

        int n = nums.size();
        vector<int> bit(32 , 0);
        int ans = 0;

        for(int i = 0 ; i < n ; i++) {
            for(int j = 0 ; j < 32 ; j++) {
                if((nums[i] & (1 << j))) {
                    bit[j]++;
                }
            }
        }

        for(int i = 0 ; i < 32 ; i++) {
            if(bit[i] && (long long)pow(bit[i] , 2) % 2 != 0) ans ^= (1 << i);
        }

        return ans;

    }
};
```

