class Solution {
public:
    int reverseNo(int n)
    {
        int ans = 0;

        while(n)
        {
            ans = (ans * 10) + (n%10);
            n/=10;
        }

        return ans;
    }

    int alternateDigitSum(int n) 
    {
        int sum = 0;

        int check = 1;             // flag variable for denoting odd position 
        int val = reverseNo(n);  // stores the original no. in reverse order 

		 // add and substracts the alternate digits of a number 

        while(val)
        {
            if(check)       // if it is odd indexed digit then add its value to the sum 
            {
                sum += val%10;  
            }

            else             // if it is even indexed digit then substract its value from the sum
            {
				sum -= val%10;
			}

            check = 1-check;

            val/=10;
        }

        return sum;        
    }
};
```

