class Solution {
public:
    int alternateDigitSum(int n) {
        vector<long long> nums;

        while(n){
            nums.push_back(n%10);
            n/=10;
        }

        int N=nums.size();

        long long sum=0;
        if(N%2 == 0){
            for(int i=0; i<N; i++){
                if(i%2 == 0) sum += (-1*(nums[i]));
                else sum += (nums[i]);
            }
        }

        else{
            for(int i=0; i<N; i++){
                if(i%2 == 1) sum += (-1*(nums[i]));
                else sum += (nums[i]);
            }

        }

        return sum;
    }
};
```

