        // so we have to reverse the array first
        reverse(v.begin(), v.end());
        int res = 0;
        for (int i = 0; i < v.size(); i ++) 
        {
            if(i%2 == 0) res += v[i]; // first index we have to consider positive, so at every alternating position, we are adding the digit and subtracting the digit.
            else res -= v[i];
        }
        return res;
    }
};
