int alternateDigitSum(int n)
{
    int count = 0,n1=n,sum=0;
    while(n1>0)
    {
        n1/=10;
        count++;
    }
    if(count%2!=0)
    n *= (-1);
    while(n!=0)
    {
        n*=(-1);
        sum+=(n%10);
        n/=10;
    }
    return sum;
}

