class Solution {
    public int alternateDigitSum(int n) {

           int sum = 0;
           int countDigit = 0;

           // Count the number of Digits in the number
           int num = n;
           while (num > 0) {

               num = num / 10;
               countDigit += 1;
           }

           char sign = '+';

           // If number of digits are even then sign of first digit will be -ve.
           if (countDigit % 2 == 0) {

               sign = '-';

           } 

           // Calculating the sum
           while (n > 0) {

               int rem = n % 10;

               if (sign == '-') {

                   sum = sum + rem*(-1);

                   sign = '+';

               } else {

                   sum = sum + rem;

                   sign = '-';
               }


               n = n/10;
           }

           return sum;
    }
}
```

