class Solution {
public:
    int alternateDigitSum(int n) {

        // first off all reverse the number

        int rev = 0;

        while(n)
        {
            rev = rev * 10 + n % 10;

            n = n / 10;
        }

        // now find the sum of reversed number according to the given conditions

        int sum = 0;

        int pos = 1;

        while(rev)
        {
            if(pos % 2 == 1)
            {
                sum += rev % 10;
            }
            else
            {
                sum -= rev % 10;
            }

            pos++;

            rev = rev / 10;
        }

        return sum;
    }
};
```

