class Solution {
    public int alternateDigitSum(int n) 
    {
        Stack<Integer> stack=new Stack<Integer>();
        List<Integer> list=new ArrayList();
        int sum=0;
        while(n>0)
        {
            int d=n%10;
            stack.add(d);
            n=n/10;
        }

        while(!stack.isEmpty())
        {
            list.add(stack.pop());    
        }
        for(int i=1;i<list.size();i+=2) //In Alternate value set -1
        {
            list.set(i,-list.get(i));
        }
        for(int i=0;i<list.size();i++)
        {
            sum+=list.get(i);
        }

        return sum;
    }
}
```

