```
class Solution {
    public int alternateDigitSum(int n) {
        int ans = 0;
        char[] arr = String.valueOf(n).toCharArray();

        for (int i = 0; i < arr.length; i++) {
            int digit = arr[i] - '0'; // Convert the character to its integer value
            if (i % 2 == 0) {
                ans += digit;
            } else {
                ans -= digit;
            }
        }
        return ans;
    }
}
