class Solution {
public:
    int alternateDigitSum(int n) {
        int ans=0;
        vector<int>v;
        while(n)
        {
            v.push_back(n%10);
            n=n/10;
        }
        reverse(v.begin(),v.end());
        bool pos=true;
        for(auto it:v)
        {
            if(pos)
            {
                ans+=it;
            }
            else
            {
                ans+=-it;
            }
            pos=!pos;
        }
        return ans;
    }
};
```

