class Solution {
public:
    int alternateDigitSum(int n) {
        int sum=0;
        int fac=1;
        stack<int> s;
        while(n>0){
            int num=n%10;
            n=n/10;
          s.push(num);
        }
        while(!s.empty()){
            sum+=s.top()*fac;
            s.pop();
            fac=fac*-1;
        }
        return sum;
    }
};
```

