class Solution:
    def fillArray(self, score, temp, n, t, idx):
        for i in range(n):
            temp[t][i] = score[idx][i]

    def merge(self, score, n, l, mid, r, k):
        p = l
        q = mid + 1
        temp = [[0] * n for _ in range(r - l + 1)]
        t = 0
        while p <= mid and q <= r:
            if score[p][k] > score[q][k]:
                self.fillArray(score, temp, n, t, p)
                p += 1
                t += 1
            else:
                self.fillArray(score, temp, n, t, q)
                q += 1
                t += 1

        while p <= mid:
            self.fillArray(score, temp, n, t, p)
            p += 1
            t += 1

        while q <= r:
            self.fillArray(score, temp, n, t, q)
            q += 1
            t += 1

        for i in range(l, r + 1):
            for j in range(n):
                score[i][j] = temp[i - l][j]

    def mergeSort(self, score, n, l, r, k):
        if l >= r:
            return

        mid = l + (r - l) // 2
        self.mergeSort(score, n, l, mid, k)
        self.mergeSort(score, n, mid + 1, r, k)
        self.merge(score, n, l, mid, r, k)

    def sortTheStudents(self, score, k):
        m = len(score)
        n = len(score[0])
        self.mergeSort(score, n, 0, m - 1, k)
        return score
