class Solution {
    int rows=0;
    public int[][] sortTheStudents(int[][] score, int k) {
        rows = score.length;
        for(int i=0;i<rows;i++){
            int[] res = maxFinder(i,k,score);
            for(int x=0;x<rows;x++){
                if(score[x][k]<res[0]){
                    for(int e=0;e<score[0].length;e++){
                            int val = score[x][e];
                            score[x][e]=score[res[1]][e];
                            score[res[1]][e]=val;
                        }
                        break;
                    }
                }
            }
        return score;
    }
    //maxFinder will return max element in array and its row
    public int[] maxFinder(int r,int k,int[][] score){ 
        int max=0;
        int rowSwap=0;
        for(int i=r;i<rows;i++){
            if(max<score[i][k]){
                max = score[i][k];
                rowSwap=i;
            }

        }
        int[] a = {max,rowSwap};
        return a;
    }
}
