    vector<vector<int>> sortTheStudents(vector<vector<int>>& score, int k) {
        int m = score.size();
        int n = score[0].size();
        // Step 1-
        for(int i=0 ; i<m ; i++){
            swap(score[i][0] , score[i][k]);
        }
        // Step 2-
        sort(score.begin() , score.end() , mycmp);
        // Step 3-
        for(int i=0 ; i<m ; i++){
            swap(score[i][0] , score[i][k]);
        }
        //step 4-
        return score;
    }
};
