class Solution {
public:

    // compare function for sorting in descending order

    static bool compare(pair<int, int>& a, pair<int, int>& b)
    {
        return a.first > b.first;
    }

    vector<vector<int>> sortTheStudents(vector<vector<int>>& score, int k) {

        int n = score.size();

        int m = score[0].size();

        // store the kth column together with row number

        vector<pair<int, int>> col(n);

        for(int i = 0; i < n; i++)
        {
            col[i] = {score[i][k], i};
        }

        // sort the kth column

        sort(col.begin(), col.end(), compare);

        // now fill the matrix according to given conditions

        vector<vector<int>> res(n);

        for(int i = 0; i < n; i++)
        {
            res[i] = score[col[i].second]; 
        }

        return res;
    }
};
```

