class Solution {
  public static int[][] sortTheStudents(int[][] score, int k) {
    var i =0;
    var j = -1;
    while(i < score.length){
      var maxIndex = findMaxIndex(score, j, k);
      j++;
      var temp = score[j];
      score[j] = score[maxIndex];
      score[maxIndex] =temp;
      i++;
    }
    return score;
  }

  private static int findMaxIndex(int[][] score,int j, int k){
    var index = -1;
    var value = -1;
    for(int i = score.length-1; i > j; i--) {
      var current = score[i][k];
      if(current > value){
        value = current;
        index = i;
      }
    }
    return index;
  }

}
```

