class Solution {
public:
    vector<vector<int>> sortTheStudents(vector<vector<int>>& score, int k) {

        int row = score.size();
        int col = score[0].size();
        int up = 0;

        //creating a vector to store the kth column elements, this will be used to find the max_element and their indexes.

        vector<int> kcol;
        for(int i = 0; i < row; ++i){
            kcol.push_back(score[i][k]);
        }

        int left = 0;
        while(left < row - 1){
            //finding the max element.
            auto maxi = max_element(kcol.begin() + left, kcol.end());

            //finding the index of the max element
            int ind = distance(kcol.begin(), maxi);

            //first of all swap the kcol array elements.
            swap(kcol[left], kcol[ind]);

            //swapping all the elements of the two rows in question.
            for(int i = 0; i < col; ++i){
                swap(score[up][i], score[ind][i]);
            }
            up++;
            left++;
        }

        return score;
    }
};

