class Solution {
public:
    vector<vector<int>> sortTheStudents(vector<vector<int>>& score, int k) {
        vector<vector<int>>ans;
        priority_queue<int>pq;
        for(int i =0;i< score.size();i++){
            pq.push(score[i][k]);

        }
        while(!pq.empty()){
            int temp= pq.top();
            pq.pop();
            for(int i =0;i< score.size();i++){
                if(score[i][k] == temp){
                    vector<int>res;
                    for(int j = 0; j< score[0].size();j++){
                        res.push_back(score[i][j]);

                    }
                    ans.push_back(res);
                }

            }
        }
        return ans;
    }
};
```

