class Solution {
    public int[][] sortTheStudents(int[][] score, int k) {
        for(int i=0;i<score.length-1;i++){
            int smallest=score[0][k];
            int p=0,q=score.length-i-1;
            for(int j=0;j<score.length-i;j++){
                if(smallest>=score[j][k]){
                    smallest=score[j][k];
                    p=j;
                }
                q=j;
            }
            if(p==q){
                continue;
            }
            for(int j=0;j<score[0].length;j++){
                smallest=score[p][j];
                score[p][j]=score[q][j];
                score[q][j]=smallest;
                }
        }
        return score;
    }
}
