        for(int i = 0 ; i<n ;i++){
            pq.push({score[i][k] , i});
        }
        vector<vector<int>> ans ;
        while(!pq.empty()){
            int index = pq.top().second;
            ans.push_back(score[index]);
            pq.pop();
        }
        return ans ;
    }
};
