class Solution {
public:
    vector<vector<int>> sortTheStudents(vector<vector<int>>& score, int k) {
        vector<pair<int, vector<int>>> v;
        vector<int>filler;
        int f;
        for(int i=0; i<score.size(); i++){
            for(int j=0; j<score[i].size(); j++){
                if(j==k){
                    f = score[i][j];
                }

                filler.push_back(score[i][j]);
            }
            v.push_back({f, filler});
            filler.clear();
        }

        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        score.clear();

        for(int i=0; i<v.size(); i++){
            for(int j=0; j<v[i].second.size(); j++){
                filler.push_back(v[i].second[j]);
            }
            score.push_back(filler);
            filler.clear();
        }

        return score;
    }
};
```

