class Solution {
    public int[][] sortTheStudents(int[][] score, int k) {

        for(int i=0; i<score.length-1; i++) {
            int rowWithMax = getMaxRow(score, k, i+1); 
            // swap it with ith row if desired
            if(score[rowWithMax][k] > score[i][k]) {
                swapRow(score, i, rowWithMax); 
            }
        }

        return score; 
    }

    int getMaxRow(int[][] arr, int k, int i) {
        int maxRow = -1;
        int maxEle = -1;  
        for(int j=i; j<arr.length; j++) {
            if(arr[j][k] > maxEle) {
                maxRow = j; 
                maxEle = arr[j][k]; 
            }
        }
        return maxRow; 
    } 

    void swapRow(int[][] score, int i, int j) {
        // swap ith row with jth row
        for(int k=0; k<score[0].length; k++) {
            int temp = score[i][k]; 
            score[i][k] = score[j][k]; 
            score[j][k] = temp; 
        }
    }
}
