class Solution:
    def putMarbles(self, weights: List[int], K: int) -> int:
        # dp[i][j] = min/max value of only considering the first j + 1 weights divided into i parts
        # dp[i][j] = min(dp[i - 1][k] + weights[k + 1] + weights[j]) for 1 <= k < j
        n = len(weights)
        min_dp = [[float('inf')] * n for _ in range(K + 1)]
        max_dp = [[float('-inf')] * n for _ in range(K + 1)]
        for i in range(n):
            min_dp[1][i] = weights[0] + weights[i]
            max_dp[1][i] = weights[0] + weights[i]

        for i in range(2, K + 1):
            for j in range(n):
                for k in range(j):
                    min_dp[i][j] = min(min_dp[i - 1][k] + weights[k + 1] + weights[j], min_dp[i][j])
                    max_dp[i][j] = max(max_dp[i - 1][k] + weights[k + 1] + weights[j], max_dp[i][j])

        return max_dp[K][n - 1] - min_dp[K][n - 1]
```
