public:
    long long putMarbles(vector<int>& weights, int k) {
        priority_queue<long long int,vector<long long int>,greater<long long int>>min_h;
        priority_queue<long long int>max_h;
        int mn=0,n=weights.size();
        for(int i=0;i<n-1;i++){
            long long int x=weights[i]+weights[i+1];
            min_h.push(x);
            max_h.push(x);
            if(min_h.size()>=k)min_h.pop();
            if(max_h.size()>=k)max_h.pop();
        }
        long long int res=0;
        while(!min_h.empty()){
            res+=min_h.top();
            min_h.pop();
        }
        while(!max_h.empty()){
            res-=max_h.top();
            max_h.pop();
        }
        return res;

    }
};
