func putMarbles(weights []int, k int) int64 {
    if k == 1 || k == len(weights) {
        return 0
    }
    min_scores, max_scores := 0, 0
    pairs_sum := make([]int, len(weights) - 1)

    for i := 0; i < len(weights) - 1; i++ {
        pairs_sum[i] = weights[i] + weights[i + 1]
    }
    sort.Ints(pairs_sum)
    for _, num := range pairs_sum[k - 1:] {
        max_scores += num
    }
    sort.Sort(sort.Reverse(sort.IntSlice(pairs_sum)))
    for _, num := range pairs_sum[k - 1:] {
        min_scores += num
    }
    return int64(max_scores - min_scores)
}
