#define ll long long
class Solution {
public:

    long long putMarbles(vector<int>& weights, int k) {
        int n=weights.size();
        vector<ll> v;
        for(int i=1;i<n;i++)
        {
            v.push_back((weights[i]+weights[i-1]));
        }
        priority_queue<ll> pq;
        priority_queue<ll,vector<ll>,greater<ll>> pq1;
        for(int i=0;i<v.size();i++)
        {
            pq.push(v[i]);
            pq1.push(v[i]);
        }
        // we have to put k-1 bars to divide them into k subarray

        // starting and ending element included in both but we have to find difference so both are subtracted 
        ll ans1=0,ans2=0;
        k--;
        while(k--)
        {
            ans1+=pq.top();
            ans2+=pq1.top();
            pq.pop();
            pq1.pop();
        }
        return ans1-ans2;



    }
};
