```
class Solution {
public:
    long long putMarbles(vector<int>& nums, int k) {
        int n = nums.size();
        if(k==1) return 0;

        priority_queue<pair<long , float>> maxheap; // stores [max_consecutive_sum,{(ind1+ind2)/2}]
		// note that (ind1+ind2)/2 is float value eg. 2.5 or 6.5 and it will give us the index of integers which make value : max_consecutive_sum
		// ind: [0 ,1,  2 ,  3, 4]
		// eg.  [1, 3 , 100 ,5 ,1]
		//sum:[1+3 = 4 ->{ind:0 + ind:1}/2 = 0.5 ]
		//sum:[3+100 = 103 -> {ind:1 + ind:2}/2 = 1.5]
		//sum[100+5 = 105 -> {ind:2 + ind:3}/2 = 2.5]
		// ... and so on hence maxheap will contain ->[{105 , 2.5} , {103 , 1.5} , {6 , 3.5} , {4 , 0.5} ]
        priority_queue<pair<long , float> , vector<pair<long , float>> , greater<pair<long , float>>> minheap;
        for(int i=1 ; i<n ; i++){
            maxheap.push({nums[i]+nums[i-1] , ((i+i-1)/2)});
            minheap.push({nums[i]+nums[i-1] , ((i+i-1)/2)});
        }
        //  hence maxheap will contain ->[ {4 , 0.5} , 6 , 3.5} , {103 , 1.5}  , {105 , 2.5} ]
        priority_queue<float , vector<float> , greater<float>> mh1 , mh2;
        for(int i=0 ; i<k-1 ; i++){
            mh1.push(maxheap.top().second); maxheap.pop();
            mh2.push(minheap.top().second); minheap.pop();
        }

        long long maxval = 0ll;
        float prev = 0;
		// finding split with maxSum , such K-1 splits will give rise to K different bags
        while(mh1.size()>0){
            float t = mh1.top(); mh1.pop(); // lets say if t = 3.5 and mh1.top.first = 100 i.e current_sum
            int v1 = t , v2 = t+1; //cout<<v1<<" "<<v2<<endl; // if t=3.5 then v1 = 3 and v2 = 4 , hence we get both indexed of this current_sum i.e 100
            maxval+=(nums[prev]+nums[v1]); //is same as image_2 -> point_4(implementation) -> whats written in red
            prev = v2;
        }
        maxval+=(nums[prev]+nums[n-1]);

        prev = 0;
        long long minval = 0ll;
		// finding split with minSum  , such K-1 splits will give rise to K different bags
        while(mh2.size()>0){
            float t = mh2.top(); mh2.pop();
            int v1 = t , v2 = t+1;
            minval+=(nums[prev]+nums[v1]);  //is same as image_2 -> point_4(implementation) -> whats written in red
            prev = v2;
        }
        minval+=(nums[prev]+nums[n-1]);

        return maxval-minval;
    }
};
```

