class Solution {
public:
    long long putMarbles(vector<int>& weights, int k) {
        int n=weights.size();
        if(n==k||k==1)
        {
            long long ans=0;
            return ans;
        }
        vector<int> diff;
       for(int i=0;i<n-1;i++)
       {
           diff.push_back(weights[i]+weights[i+1]);
       } 

       sort(diff.begin(),diff.end());
       n=diff.size();
       long long mini=0,maxi=0;
       for(int i=0;i<k-1;i++)
       {
           mini+=diff[i];
           maxi+=diff[n-i-1];
       }

       long long ans=maxi-mini;

       return ans;
    }
};
