class Solution {
  int putMarbles(List<int> weights, int k) {
    if (--k == 0) return 0;

    List<int> adjs = [];
    for (int i = 1; i < weights.length; i++)
      adjs.add(weights[i - 1] + weights[i]);

    adjs.sort();

    var res = 0;

    for (int i = adjs.length - 1; i >= adjs.length - k; i--)
      res += adjs[i];
    for (int i = 0; i < k; i++)
      res -= adjs[i];

    return res;
  }
}
