class Solution {
public:
    long long putMarbles(vector<int>& weights, int k) {
        priority_queue<long,vector<long>,greater<long>> minPQ;
        priority_queue<long> maxPQ;
        int n = weights.size(), x = 0;
        long maxi = 0, mini = 0;
        for(int i = 0 ; i < n - 1 ; i++){
            x = weights[i] + weights[i + 1];
            minPQ.push(x);
            maxPQ.push(x);
            if(minPQ.size() > k - 1){
                minPQ.pop();
                maxPQ.pop();
            }
        }
        while(maxPQ.size()){
            mini += maxPQ.top();
            maxi += minPQ.top();
            maxPQ.pop();
            minPQ.pop();
        }
        return maxi - mini;
    }
};
