class Solution:
    def putMarbles(self, weights: List[int], k: int) -> int:
        break_sums = []
        prev = weights[0]

        for i in range(1, len(weights)):
            break_sums.append(prev + weights[i])
            prev = weights[i]

        break_sums.sort()
        return (sum(break_sums[len(weights) - k:]) - sum(break_sums[:k - 1]))
