    long long putMarbles(vector<int>& weights, int k) {
        vector<long long>v;
        for(int i= 1; i <weights.size(); i++){
            v.push_back(weights[i-1]+weights[i]);
        }
        sort(v.begin(),v.end());
        k--;
        int left = 0, right = v.size()-1;
        long long ans = 0;
        while(k > 0){
            ans += v[right]-v[left];
            right--;
            left++;
            k--;
        }
        return ans;
