```cpp
class Solution {
public:
    int maxCount(vector<int>& banned, int n, int maxSum) {
        // put all banned elements to a set
        set<int> s(banned.begin(), banned.end());
        int sum = 0, ans = 0;
        // try each possible number (rule #1 & #2)
        for (int i = 1; i <= n; i++) {
            // if the number `i` is banned, then we skip it (rule #3)
            if (s.count(i)) continue;
            // we can include this number as long as it doesn't exceed `maxSum` (rule #4)
            if (sum + i > maxSum) break;
            // add to current sum
            sum += i;
            // increase ans by 1 as we choose number `i`
            ans += 1;
        }
        return ans;
    }
};
```

