class Solution {
    public int maxCount(int[] banned, int n, int maxSum) {
        // create a set and all elements to the set
        // this will help us know what numbers are banned
        Set<Integer> set = new HashSet<>();
        for (int i : banned) {
            set.add(i);
        }

        int count = 0;  // variable to store our answer
        for (int i = 1; i <= n; i++) {
            // if number is in set, means its banned, therefore skip
            if (set.contains(i)) {
                continue; 
            }
            // i can be counted in if its less than 'maxSum'
            if (i <= maxSum) {
                count++;      // increment count
                maxSum -= i;  // subtract i from maxSum (doing a + b = c is same as a = c - b)
            } 
            // otherwise break out, because we don't want to check further
            // the smaller numbers we count in, the greater the value of 'count' is going to be
            else {    
                break;
            }            
        }

        return count;
    }
}
